import React, {Component} from 'react';
import './App.css';
import axios from 'axios';

const enteredSKU = prompt('Please enter the SKU')

const api = axios.create({
  baseURL: "https://akshi.pythonanywhere.com/product/{enteredSKU}"
})

class App extends Component {
  state = {
    product : ""
  }
  constructor () {
    super();
    api.get(`/`).then(res => {
      console.log(res.data)
      this.setState({ product : res.data})
    })
  }
  render() {
    return (
        <div className="App">
          <header className="App-header">
            <h1>SKU: {enteredSKU}</h1>
            <h2>Price: {this.state.product.Price}</h2>
            <h2>Last Updated: {this.state.product.LastUpdated}</h2>
          </header>
        </div>
    );
  }
}

export default App;
